class Solution:
    def ladderLength(self, beginWord: str, endWord: str, wordList: List[str]) -> int:
        """
        graph BFS
        不能用回溯, 因为不是固定顺序的更换
        """
        n = len(beginWord)

        neibMap = {}
        for word in wordList:
            for i in range(n):
                key = word[:i] + '*' + word[i+1:]
                neibMap.setdefault(key, [])
                neibMap[key].append(word)

        # print(neibMap)
        def getNeig(curWord):
            neibSet = set()
            for i in range(n):
                neibKey = curWord[:i] + '*' + curWord[i + 1:]
                candLis = neibMap.get(neibKey, [])
                neibSet = neibSet.union(candLis)
            return neibSet
        # endFunc

        from collections import deque

        myQueue = deque([(beginWord, 1)])
        visitedNode = set()
        while myQueue:
            curNode, level = myQueue.popleft()
            visitedNode.add(curNode)
            neibSet = getNeig(curNode)
            for neib in neibSet:
                if neib == endWord:
                    return level + 1
                if neib in visitedNode:
                    continue
                visitedNode.add(neib)
                myQueue.append((neib, level + 1))
        return 0
    # endFunc
# endClass

